/**  * StringTokenIO.java * * Description:		uses stringtokenizer and buffered reader * @author			Wu 211 009 * @version			 */package StringTokenIO;import java.io.BufferedReader;//import java.io.InputStreamReader;//import java.io.IOException;import java.util.StringTokenizer;//import java.io.FileReader;import java.io.*;public class StringTokenIO {	public StringTokenIO()   // class constructor	{	}	// Main entry point -- a hook for JVM into this class	static public void main(String[] args) 	{		new StringTokenIO();  // calls the class constructor				BufferedReader console = new BufferedReader(new InputStreamReader(System.in)); 		String input;		System.out.print("Enter a string: ");		try 		{			input = console.readLine(); 		} 				catch (IOException e)		{			input = new String("-- An error has occurred --");		}				System.out.println("\nYou entered " + input + ".");				// stringtokenizer		StringTokenizer st = new StringTokenizer("Christian 34 2 7");		    	while (st.hasMoreTokens())    	 {       		System.out.println(st.nextToken());     	 }			// call fileio that couples user input with stringtokenizer and bufferedreader		fileio();		usingfiles();	}	static void fileio()	{		String name[] = new String[3];		Integer age[] = new Integer[3];		Integer child[] = new Integer[3];		Integer teachyrs[] = new Integer[3];		String hold[] = new String[4];		int s = 0;		int x = 0;								BufferedReader console = new BufferedReader(new InputStreamReader(System.in)); 		String input;				while(x < 3)		{			System.out.print("Enter a Name, Age, Children and Years Teaching: ");			try 			{				input = console.readLine(); 			} 					catch (IOException e)			{				input = new String("-- An error has occurred --");			}					// stringtokenizer			StringTokenizer st = new StringTokenizer(input);					s = 0;    		while (st.hasMoreTokens())    	 	{       			//System.out.println(st.nextToken());       			hold[s] = st.nextToken();       			s++;     		}     		//Integer d = new Integer(2);     		//d.parseInt(hold[1]);     		     		     		System.out.println(hold[0] + " " + hold[1] + " " + hold[2] + " " + hold[3]);     		     		name[x] = hold[0];     		age[x] = new Integer(Integer.parseInt(hold[1]));     		child[x] = new Integer(Integer.parseInt(hold[2]));     		teachyrs[x] = new Integer(Integer.parseInt(hold[3]));     		     		     		x++;				}		// display first set of data to verify sucess		System.out.println(name[0] + " " + age[0] + " " + child[0] + " " + teachyrs[0]);				return;	}		// read from a file using JAVA.IO.FILEREADER	// filereader class inherits from inputstream reader	// so, process the file as you processed from the console	// addtrss.txt 	// joe eastlake nj	// sam southpark co	static void usingfiles()	{				String filename = new String("address.txt");		String linein;		String hold[] = new String[3];				// instantiate and set instance of filereader to the file to be processed		FileReader reader; 		try		{			reader = new FileReader(filename);		}		catch(FileNotFoundException e)		{			System.out.println("file read error");			return;		}				// instiantate a uffer to hold a chunk of the file		BufferedReader br = new BufferedReader(reader); 				// read in 1 line of the file into the buffer		try 			{				linein = br.readLine(); 			} 					catch (IOException e)			{				linein = new String("-- An error has occurred --");			}					// stringtokenizer -- parse the line stopping at white space				// read in 1 line		StringTokenizer st = new StringTokenizer(linein);						// parse the buffer			hold[0] = st.nextToken();		hold[1] = st.nextToken();		hold[2] = st.nextToken();				// display the first line in the file		System.out.println(hold[0] + " " + hold[1] + " " + hold[2] );		return;	}	}