/**  * money.java * * Title:			inherit from java lang number * Description:		extend the java.lang.number abstract class.  with abstract classes, you MUST implement EACH abstract method. * @author			Wu 211 009 * @version			 *///package testinheritance;import MoneyParent;public class money extends Object{  // extends Object --- this is IMPLICIT as ALL									// java classes INHERIT from Object. I just added this									// to show it 	public money() {	}	// Main entry point	static public void main(String[] args) {		new money();				String name;		double balance;		EasyReader console = new EasyReader();				Dollar[] aDollar = new Dollar[2];						// Get Customer information		System.out.print("\nEnter Customers Name: ");		name = console.readWord();				System.out.print("\nEnter Customers Initial Balance: ");		//balance = console.readDouble();				Double.parseDouble(console.readWord());						//Dollar aDollar = new Dollar(balance, name);		aDollar[0] =  new Dollar(balance, name);						// apply interest to the balance if the customer has a balance over $1,000.00				if (aDollar[0].getBalance() > 1000.00)			{			aDollar[0].setInterest(.023);			aDollar[0].applyInterest();			System.out.println(aDollar[0].getName() + " , " + "You have added interest of " +				aDollar[0].getInterest());			System.out.println("For a new balance of " + aDollar[0].toString());				}			else				System.out.println("Your Current balance is " + aDollar[0].toString());									// Test succesful inheritance from class Object.		System.out.println(aDollar[0].getName() +  " The integer value of your balance is " + 			aDollar[0].intValue());								// Create a second customer			System.out.print("\nEnter Customers Name: ");		name = console.readWord();				aDollar[1] =  new Dollar(name);  // this constructor defaults balance to 500.0				System.out.println(aDollar[1].getName() + " , " +  " Your Current balance is " + 			aDollar[1].toString());								// Compare the two objects nased on their balances				int a = aDollar[0].compareTo(aDollar[1]);				if (a == 1)			System.out.println(aDollar[0].toString() + " is greater than " +				aDollar[1].toString());			else if(a == 0)			System.out.println(aDollar[0].toString() + " is the same as  " +				aDollar[1].toString());			else 			System.out.println(aDollar[0].toString() + " is less than " +				aDollar[1].toString());	}	}